<?phprequire "./forms.php";session_start();$file = 'db.txt';$db = fopen($file, 'a+');file_put_contents($file, '');$arr = $_SESSION['arr'];$line = json_decode($arr[$_POST['index']], true);$line['name'] = check($_POST['login']);$line['email'] = check($_POST['email']);$line['desc'] = check($_POST['desc']);$arr[$_POST['index']] = json_encode($line);foreach ($arr as $val) {    fwrite($db, $val . "\n");}?>